mapper.insert:
INSERT INTO
    swimpool_tbl ( name
                 , lanes
                 , lane_type
                 , phone_number
                 , addr)
VALUES (
         #{name}
       , #{lanes}
       , #{laneType}
       , #{phoneNumber}
       , #{addr}
       )

mapper.update:
UPDATE swimpool_tbl SET
    name = #{name}
  , lanes = #{lanes}
  , lane_type = #{laneType}
  , phone_number =#{phoneNumber}
  , addr = #{addr}
WHERE id = #{id}
